package runable;

public class Declare_Initialize {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Declare (Data type>>Variable ) -> we don't include value
		String myString;
		int myInt;
		double myDouble;
		
		// Initialized value(Variable >> Value)
		myString = "Ginzabe";
		myInt = 8;
		myDouble = 8.0;
		
	}

}
